import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Table, Button, Form, Modal, Alert, Spinner } from 'react-bootstrap';
import { toast } from 'react-toastify';
import axios from 'axios';

const Trading = () => {
  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [selectedStock, setSelectedStock] = useState(null);
  const [tradeType, setTradeType] = useState('buy');
  const [quantity, setQuantity] = useState(1);
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    fetchStocks();
  }, []);

  const fetchStocks = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/stocks', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setStocks(response.data);
    } catch (error) {
      toast.error('Failed to load stocks');
      console.error('Stocks fetch error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTrade = (stock, type) => {
    setSelectedStock(stock);
    setTradeType(type);
    setQuantity(1);
    setShowModal(true);
  };

  const executeTrade = async () => {
    if (!selectedStock || quantity < 1) {
      toast.error('Please enter a valid quantity');
      return;
    }

    setSubmitting(true);
    try {
      const token = localStorage.getItem('token');
      const endpoint = tradeType === 'buy' ? '/api/trades/buy' : '/api/trades/sell';

      await axios.post(endpoint, {
        stockSymbol: selectedStock.symbol,
        quantity: parseInt(quantity)
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      toast.success(`${tradeType === 'buy' ? 'Purchase' : 'Sale'} successful!`);
      setShowModal(false);
      setQuantity(1);
    } catch (error) {
      toast.error(error.response?.data?.error || `${tradeType} failed`);
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <Container className="d-flex justify-content-center align-items-center" style={{ minHeight: '400px' }}>
        <Spinner animation="border" variant="primary" />
      </Container>
    );
  }

  return (
    <Container fluid>
      <Row>
        <Col>
          <h1 className="mb-4">Stock Trading</h1>

          <Card>
            <Card.Header>
              <h5 className="mb-0">Available Stocks</h5>
            </Card.Header>
            <Card.Body>
              {stocks.length > 0 ? (
                <Table responsive striped hover>
                  <thead>
                    <tr>
                      <th>Symbol</th>
                      <th>Company</th>
                      <th>Current Price</th>
                      <th>Change</th>
                      <th>Sector</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stocks.map((stock) => {
                      const change = stock.currentPrice - stock.previousClose;
                      const changePercent = (change / stock.previousClose) * 100;

                      return (
                        <tr key={stock.symbol}>
                          <td><strong>{stock.symbol}</strong></td>
                          <td>{stock.companyName}</td>
                          <td>${stock.currentPrice?.toFixed(2)}</td>
                          <td className={change >= 0 ? 'text-success' : 'text-danger'}>
                            {change >= 0 ? '+' : ''}${change.toFixed(2)} ({changePercent.toFixed(2)}%)
                          </td>
                          <td>{stock.sector}</td>
                          <td>
                            <Button
                              variant="success"
                              size="sm"
                              className="me-2"
                              onClick={() => handleTrade(stock, 'buy')}
                            >
                              Buy
                            </Button>
                            <Button
                              variant="danger"
                              size="sm"
                              onClick={() => handleTrade(stock, 'sell')}
                            >
                              Sell
                            </Button>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </Table>
              ) : (
                <div className="text-center py-4">
                  <p className="text-muted">No stocks available for trading.</p>
                </div>
              )}
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Trading Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>
            {tradeType === 'buy' ? 'Buy' : 'Sell'} {selectedStock?.symbol}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {selectedStock && (
            <>
              <div className="mb-3">
                <strong>{selectedStock.companyName}</strong>
                <br />
                <span className="text-muted">Current Price: ${selectedStock.currentPrice?.toFixed(2)}</span>
              </div>

              <Form.Group className="mb-3">
                <Form.Label>Quantity</Form.Label>
                <Form.Control
                  type="number"
                  min="1"
                  value={quantity}
                  onChange={(e) => setQuantity(e.target.value)}
                />
              </Form.Group>

              <div className="mb-3">
                <strong>Total: ${(selectedStock.currentPrice * quantity).toFixed(2)}</strong>
              </div>
            </>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Cancel
          </Button>
          <Button
            variant={tradeType === 'buy' ? 'success' : 'danger'}
            onClick={executeTrade}
            disabled={submitting}
          >
            {submitting ? (
              <>
                <Spinner animation="border" size="sm" className="me-2" />
                Processing...
              </>
            ) : (
              `${tradeType === 'buy' ? 'Buy' : 'Sell'} ${quantity} shares`
            )}
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
};

export default Trading;
